mapper.select:
select * from recmv_rev
order by createDate desc
limit #{beginNumOfItems}, #{numOfItemsPerPage}

mapper.update:
update recmv_rev 
set title = #{rev.title}, content = #{rev.content}, rating = #{rev.rating}, updateDate = now()
where num = #{num}

update recmv_movie 
set rating = #{rating}
where title = #{title}

mapper.select:
select * from recmv_rev
where userNum = #{userNum}
order by createDate desc
limit #{cri.beginNumOfItems}, #{cri.numOfItemsPerPage}
